// CF: https://polygon.io/docs/crypto/get_v2_snapshot_locale_global_markets_crypto_tickers
export const snapshotAllTickers = (get, query, options) => get(`/v2/snapshot/locale/global/markets/crypto/tickers`, query, options);
// CF: https://polygon.io/docs/crypto/get_v2_snapshot_locale_global_markets_crypto__direction
export const snapshotGainersLosers = (get, direction, query, options) => get(`/v2/snapshot/locale/global/markets/crypto/${direction}`, query, options);
// CF: https://polygon.io/docs/crypto/get_v2_snapshot_locale_global_markets_crypto_tickers__ticker
export const snapshotTicker = (get, symbol, query, options) => get(`/v2/snapshot/locale/global/markets/crypto/tickers/${symbol}`, query, options);
// CF: https://polygon.io/docs/crypto/get_v2_snapshot_locale_global_markets_crypto_tickers__ticker__book
export const snapshotTickerFullBookL2 = (get, symbol, query, options) => get(`/v2/snapshot/locale/global/markets/crypto/tickers/${symbol}/book`, query, options);
