// CF: https://polygon.io/docs/forex/get_v2_snapshot_locale_global_markets_forex_tickers
export const snapshotAllTickers = async (get, query, options) => get(`/v2/snapshot/locale/global/markets/forex/tickers`, query, options);
// CF: https://polygon.io/docs/forex/get_v2_snapshot_locale_global_markets_forex__direction
export const snapshotGainersLosers = async (get, direction, query, options) => get(`/v2/snapshot/locale/global/markets/forex/${direction}`, query, options);
// CF: https://polygon.io/docs/forex/get_v2_snapshot_locale_global_markets_forex_tickers__ticker
export const snapshotTicker = async (get, symbol, query, options) => get(`/v2/snapshot/locale/global/markets/forex/tickers/${symbol}`, query, options);
