// CF: https://polygon.io/docs/options/get_v3_snapshot_options__underlyingAsset___optionContract
export const snapshotOptionChain = async (get, underlyingAsset, query, options) => get(`/v3/snapshot/options/${underlyingAsset}`, query, options);
export const snapshotOptionContract = async (get, underlyingAsset, optionContract, query, options) => get(`/v3/snapshot/options/${underlyingAsset}/${optionContract}`, query, options);
