// CF: https://polygon.io/docs/stocks/get_v2_snapshot_locale_us_markets_stocks_tickers
export const snapshotAllTickers = async (get, query, options) => get(`/v2/snapshot/locale/us/markets/stocks/tickers`, query, options);
// CF: https://polygon.io/docs/stocks/get_v2_snapshot_locale_us_markets_stocks__direction
export const snapshotGainersLosers = async (get, direction, query, options) => get(`/v2/snapshot/locale/us/markets/stocks/${direction}`, query, options);
// CF: https://polygon.io/docs/stocks/get_v2_snapshot_locale_us_markets_stocks_tickers__stocksTicker
export const snapshotTicker = async (get, symbol, query, options) => get(`/v2/snapshot/locale/us/markets/stocks/tickers/${symbol}`, query, options);
